class History extends PluginHistory
{
  const MODEL_DEPARTMENTS = 'departments';
  const MODEL_HANDLING= 'handling';
  const MODEL_IDEA= 'idea';
  //const MODEL_TECHNICAL_PARAM_DEPARTMENTS = 'technical_param_departments';
  
  public function preSave($event) 
  {
    $user_id = GlobalFunctions::getUserId();
    
    $this->setUserId($user_id);
    $this->setCreatedatetime(date("Y-m-d H:i:s"));

    parent::preSave($event);
  }
  
  public function getCreatedatetimeGood()
  {
    return format_date($this->getCreatedatetime(), 'dd.MM.yyyy, HH:mm', 'ru');
  }
}