class TechnicalParamDepartments extends PluginTechnicalParamDepartments
{
  public function History($field = null, $toString  = null, $oldValue = null)
  {
    if (!$field||!$toString) return null;  
    
    if (!sizeof($this->_oldValues))
    {
      return;
    }
    $history = new History();
    
    $history->setModelName(History::MODEL_DEPARTMENTS);
    $history->setModelId($this->getDepartmentId());
    $history->setFieldName($field.'_tech_param');
    
    $history->setMore($this->getTechnicalParamType()->getName());
    $history->setOldValue($this->_oldValues[$field]);
    $history->setValue($this->$toString());
    $history->save();
    
    //$history->set
  }
}