<?php

/**
 * statusTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class statusTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object statusTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('status');
    }
    
    public function getStatusOpen()
    {
       $q = Doctrine_Core::getTable('status')
       ->createQuery('s')
        ->where('s.stakey = \''.sfConfig::get('status_key_open').'\'')
        ->fetchOne();
        return $q;  
    }
    
   public function getAllSmetaStatuses()
   {
       $q = Doctrine::getTable('status')
       ->createQuery('s')
       ->where('s.stakey LIKE \''.sfConfig::get('status_smeta_key').'%\'')
       ->execute();
       return $q;
   } 
   public function getAllNonSmetaStatuses()
   {
       $q = Doctrine::getTable('status')
       ->createQuery('s')
       ->where('s.stakey NOT LIKE \''.sfConfig::get('status_smeta_key').'%\'')
       ->execute();
       return $q;
   }
}